event.script:

//choose a starting cell from those along map's borders
var side = _digger_.randomI(0, 4);
var offset = _digger_.randomI(0, _digger_.numCellsPerSide());

switch (side) {
case 0: //north
    _digger_.setCurrentCell(offset, 0, _depth_);
    break;
case 1: //east
    _digger_.setCurrentCell(_digger_.numCellsPerSide() - 1, offset, _depth_);
    break;
case 2: //south
    _digger_.setCurrentCell(offset, _digger_.numCellsPerSide() - 1, _depth_);
    break;
case 3: //west
    _digger_.setCurrentCell(0, offset, _depth_);
    break;
}

