room.generator:

	    var xOffset = _digger_.randomI(0, _digger_.cellSize()/2);
        var yOffset = _digger_.randomI(0, _digger_.cellSize()/2);
        var remainingRoom = Math.min(_digger_.cellSize() - xOffset, _digger_.cellSize() - yOffset); 
        var size = _digger_.randomI(4, remainingRoom);
        var floorMaterial = _mat_DIRT_FLOOR_0;
        var wallMaterial = _mat_DIRT_BLOCK_0;

        if (_args_.x != undefined) {
           xOffset = _args_.x; 
        }

        if (_args_.y != undefined) {
           yOffset = _args_.y; 
        }

        if (_args_.size != undefined) {
           size = _args_.size; 
        }

        if (_args_.floor != undefined) {
           floorMaterial = _args_.floor; 
        }

        if (_args_.wall != undefined) {
           wallMaterial = _args_.wall; 
        }

	    function isFloor(probability) {
	        var randomProb = _digger_.randomI(0, 100);
	        if (randomProb < probability)
	            return false;
	        else
	            return true;
	    };

	    function generation(size, r1Cutoff, r2Cutoff, helperGrid, secondPass) {
	        for (var yi = yOffset + 1; yi < yOffset + size - 1; yi++) {
	            for (var xi = xOffset + 1; xi < xOffset + size - 1; xi++) {

	                var adjcount_r1 = 0;
	                var adjcount_r2 = 0;

	                for (var ii = -1; ii <= 1; ii++) {
	                    for (var jj = -1; jj <= 1; jj++) {
	                        var material = _digger_.cellTileMaterial(xi+jj, yi+ii);
	                        if (!material.equals(wallMaterial))
	                             adjcount_r1++;
	                    }
	                }

	                for (var ii = yi-2; ii <= yi+2; ii++) {
	                    for (var jj = xi-2; jj <= xi+2; jj++) {
	                        if (Math.abs(ii-yi) == 2 && Math.abs(jj-xi) == 2)
	                            continue;

	                        if (ii < 0 || jj < 0 || ii >= size || jj >= size)
	                            continue;

	                        var material = _digger_.cellTileMaterial(jj, ii);
	                        if (!material.equals(wallMaterial))
	                             adjcount_r2++;
	                    }
	                }

	                if (!secondPass) {
	                    if(adjcount_r1 >= r1Cutoff || adjcount_r2 <= r2Cutoff)
	                        helperGrid[xi][yi] =  floorMaterial; 
	                    else
	                        helperGrid[xi][yi] =  wallMaterial;
	               }
	               else {
	                   if(adjcount_r1 >= r1Cutoff)
	                        helperGrid[xi][yi] =  floorMaterial; 
	                    else
	                        helperGrid[xi][yi] =  wallMaterial;
	               }
	            }
	        }

	        for (var yi = yOffset + 1; yi < yOffset + size - 1; yi++) {
                for (var xi = xOffset + 1; xi < xOffset + size - 1; xi++) {
                    _digger_.digCellTile(xi, yi, helperGrid[xi][yi]);
                }
            }
	    }

	    var helperGrid = new Array();
        for (var x = xOffset; x < xOffset + size; x++) {
            helperGrid[x] = new Array();
            for (var y = yOffset; y < yOffset + size; y++) {
                //init helper grid
                helperGrid[x][y] =  _digger_.cellTileMaterial(x, y);

                //init cell map
                if (isFloor(40))
                    _digger_.digCellTile(x, y, floorMaterial);
            }
        }

	    for (var i = xOffset; i < xOffset + size; i++) {
	       _digger_.digCellTile(i, yOffset,  wallMaterial);
	       _digger_.digCellTile(i, yOffset + size-1,  wallMaterial);
	    }

	    for (var i = yOffset; i < yOffset + size; i++) {
           _digger_.digCellTile(xOffset, i,  wallMaterial);
           _digger_.digCellTile(xOffset + size-1, i,  wallMaterial);
        }

	    for (var i = 0; i < 4; i++) {
	        generation(size, 5, 2, helperGrid, false);
	    }

	    for (var i = 0; i < 3; i++) {
	        generation(size, 5, 2, helperGrid, true);
	    }
